oxy_webhelp_navlinks = {"digital_banking_main_solution-d4618e23":{"topics":[{"title":"Digital banking building blocks ","shortdesc":"<p class=\"shortdesc\">Finshapeâ€™s main product is a custom Digital Banking solution. Custom in this context         refers to a specific configuration of the provided building blocks (functional capabilities)         combined with developing front-end applications and component extensions. These building         blocks are not standalone products but rather DBOS infrastructure elements, DBOS components,         connectors (APIs), tools, and applications from which the final product can be         assembled.<\/p>","href":"topics\/db_modules.html","attributes":{"data-id":"db_modules"},"menu":{"hasChildren":true},"tocID":"db_modules-d4618e34","next":"db_modules-d4618e34"},{"title":"Products that client pay for","shortdesc":"<p class=\"shortdesc\">We offer to our clients three standalone products (solutions), some add-ons to the         Core Digital Banking solution, and services. Standalone means that product may be delivered         to a client on Project level and doesn't depend on other DBOS components.<\/p>","href":"topics\/finshape_products_clients_pay_for.html","attributes":{"data-id":"finshape_products_clients_pay_for"},"menu":{"hasChildren":true},"tocID":"finshape_products_clients_pay_for-d4618e1356","next":"finshape_products_clients_pay_for-d4618e1356"}]}};